import React, { useState, useEffect } from "react";
import Wen from "../svg/Wen";

import axios from "axios";

const Offers = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [card, setCard] = useState([]);

  const fetchData = async () => {
    try {
      const response = await axios.get(
        "https://leboncoin-api.herokuapp.com/api/offer/with-count"
      );
      setCard(response.data.offers);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <section>
      <div className="container pv-50 green">
        <div className="search--box br-5">
          <div className="search">
            <Wen />
            <input type="text" size="25" placeholder="Que recherchez-vous ?" />
          </div>

          <button className="search--button--blue">Rechercher</button>
        </div>
      </div>
      {/* OFFERS */}
      <div className="container offers">
        <article className=" box-shadow overflow-hidden br-5 mb-20">
          <div className="bg-silver d-flex">
            <img
              src="https://img6.leboncoin.fr/ad-large/a45ff6ecfb0289c2809d971c33bbf54c786ea12d.jpg"
              alt=""
            />
          </div>

          <ul className="blue description p-15 inside--description">
            <div className="top-description">
              <li className="title">Title</li>
              <li className="price">Price</li>
            </div>

            <div className="bottom-description">
              <li>Date</li>
            </div>
          </ul>
        </article>
      </div>
    </section>
  );
};
export default Offers;
