import React, { useState } from "react";
import Watch from "../svg/Watch";
import Cloche from "../svg/Cloche";
import Eye from "../svg/Eye";

const SignUp = () => {
  const [pseudo, setPseudo] = useState("");
  const [mail, setMail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  return (
    <div className="container">
      <div className="inside--signup">
        <div className="left--signup green">
          <h2>Pour créer un compte ?</h2>
          <div className="Watch">
            <Watch />
            <p>Gagnez du temps</p>
          </div>
        </div>

        {/* SEPARATEUR */}
        <div className="right--signup blue">
          <h2>Créez un compte</h2>
          <form>
            <div className="pseudo-email">
              <p>Pseudo *</p>
              <input type="text" />
              <p>Adresse email *</p>
              <input type="text" />
            </div>

            <div className="mdp">
              <div>
                <p>Mot de passe *</p>
                <input type="text" />
              </div>
              <div>
                <p>Confimer le mot de passe*</p>
                <input type="text" />
              </div>
            </div>

            <br />
            {/* <div className="checkbox"> */}
            <input type="checkbox" />
            {/* </div> */}

            <span>
              « J’accepte les Conditions Générales de Vente et  les Conditions
              Générales d’Utilisation »
            </span>
          </form>
        </div>
      </div>
    </div>
  );
};
export default SignUp;
